import gql from 'graphql-tag';

const SINGLE_ITEM_QUERY = gql`
  query {
    Product(where: { id: "60830f4125154338247d0456" }) {
      name
      price
      description
    }
  }
`;

export default function SingleProduct({ query }) {
  return <p>Hey im a single product {query.id} </p>;
}
